      function next (err) {
        if(err && n > 0) { n = -1; throw err }
        if(--n) return
        rpc.publish(data, function (err, ret) {
          if(err) throw err
          console.log(JSON.stringify(ret, null, 2))
          process.exit()
        })
      }

